import dayjs from 'dayjs';
import { findOrder, getEmailParam, missingOrder } from '../utils';
import { MissingOrder, NavBar, H1 } from '../components';
import Text from '../styled-components/text';

const DRINK_COST = 1000;
const MEAL_COST = 2500;

const ReceiptScreen = () => {
  const order = findOrder(getEmailParam());

  const renderMeals = () => {
    const meals = [];
    for (let i = 0; i < order.personCount; i++) {
      meals.push(
        <p key={i}>
          {order.meal.strMeal} at {MEAL_COST} isk.
        </p>
      );
    }
    return meals;
  };

  if (missingOrder()) return <MissingOrder />;

  return (
    <div>
      <NavBar page="receipt" />
      <div className="receipt-container">
        {' '}
        <H1 text={'meal'}></H1>
        {renderMeals()}
        <H1 text={'drinks'}></H1>
        {order.drinks.map((item, index) => (
          <Text key={index}>
            {item.name} at {DRINK_COST} isk.
          </Text>
        ))}
        <H1 text={'order'}></H1>
        <p>People count: {order.personCount}</p>
        <p>Date: {dayjs(order.selectedDate).format('MMMM D, YYYY, HH:mm')}</p>
        <p>Email: {order.email}</p>
        <H1 text={'total'}></H1>
        <p>{order.personCount * MEAL_COST + order.drinks.length * DRINK_COST + ' kr.'}</p>
      </div>
    </div>
  );
};

export default ReceiptScreen;
